SELECT session_id AS sessionId, user_id AS userId, user_username AS username, consultant_id AS consultantId, consultant_name AS consultantName, start_time AS startTime, end_time AS endTime,rating,feedback FROM ConsultationSessionsWithNames WHERE consultant_id = #{consultantId} AND session_status='completed'
AND start_time >= #{startTime}
AND start_time <= #{endTime}

